import { useState } from "react";
import "./ExpenseForm.css";
const ExpenseForm = () => {
  const [enteredTitle,setEnteredTitle]=useState('');
  const [enteredPrice,setEnteredPrice]=useState('');
  const [enteredDate, setEnteredDate]=useState('')
  
  const titleChangeHandler =(event)=>{setEnteredTitle(event.target.value);}
  const priceChangeHandler = (event)=>{setEnteredPrice(event.target.value);}
  const dateChangeHandler = (event)=>{setEnteredDate(event.target.value);}

  const sumitHandler= (event)=>{
    event.preventDefault();

    const expenseData={
      title: enteredTitle,
      amount: enteredPrice,
      date: new Date(enteredDate)

      
    }
    console.log(expenseData);

  }
  return (
    <form onSubmit={sumitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler}/>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={priceChangeHandler}/>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2019-01-01" max="2023-Dec-31" onChange={dateChangeHandler}/>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};
export default ExpenseForm;
